<?php

/**
 * Slider_Model_Doctrine_SliderTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class Slider_Model_Doctrine_SliderTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object Slider_Model_Doctrine_SliderTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('Slider_Model_Doctrine_Slider');
    }
    
    public function getFullSliderQuery() {
        $q = $this->createQuery('s')
                ->select('s.*')
               // ->from('Slider_Model_Doctrine_SliderSerwis7 s INDEXBY s.id')
                ->addSelect('sl.*')
                ->addSelect('l.*')
                ->addSelect('pr.*')
                ->leftJoin('s.Slides sl')
                ->leftJoin('sl.Layers l')
                ->leftJoin('sl.PhotoRoot pr')
                ;
        return $q;
    }
}